window.addEventListener('DOMContentLoaded', () => {
    const ioConfiguration = {
        root: document.getElementById("terms-container"),
        rootMargin: '80px 0px 361px 0px',
    };

    const observer = new IntersectionObserver(entries => {
        entries.forEach(entry => {
            const id = entry.target.getAttribute('id');
            if (entry.intersectionRatio > 0) {
                $(`a[href="#${id}"]`).addClass('font-medium');
                $(`a[href="#${id}"]`).addClass('text-sky-800');
                $(`a[href="#${id}"]`).addClass('text-base');
                $(`a[href="#${id}"]`).removeClass('text-gray-400');
            } else {
                $(`a[href="#${id}"]`).removeClass('font-medium');
                $(`a[href="#${id}"]`).removeClass('text-sky-800');
                $(`a[href="#${id}"]`).removeClass('text-base');
                $(`a[href="#${id}"]`).addClass('text-gray-400');
            }
        });
    });

    // Track all sections that have an `id` applied
    document.querySelectorAll('section[id]').forEach((section) => {
        observer.observe(section);
    });

});