window.addEventListener('DOMContentLoaded', () => {

    const observer = new IntersectionObserver(entries => {
        entries.forEach(entry => {
            const id = entry.target.getAttribute('id');
            if (entry.intersectionRatio > 0) {
                $(`a[id="${id}"]`).addClass('font-medium');
                $(`a[id="${id}"]`).addClass('text-sky-800');
                $(`a[id="${id}"]`).addClass('text-base');
                $(`a[id="${id}"]`).removeClass('text-gray-400');
            } else {
                $(`a[id="${id}"]`).removeClass('font-medium');
                $(`a[id="${id}"]`).removeClass('text-sky-800');
                $(`a[id="${id}"]`).removeClass('text-base');
                $(`a[id="${id}"]`).addClass('text-gray-400');
            }
        });
    });

    // Track all sections that have an `id` applied
    document.querySelectorAll('section[id]').forEach((section) => {
        observer.observe(section);
    });


});

function handleViewPort(e) {
    var target = $(`section[id='${e}']`).position();
    window.scrollTo(0, target.top - 20);
}
